-- ============================================
-- SEED TOPICS DATA
-- Imports the 12 writing topics from the existing topics.ts file
-- ============================================

INSERT INTO public.topics (id, title, prompt, category, inspiration) VALUES
('1', '梦境之地', '描写一个你从未去过但经常梦见的地方。', 'imagination', '{"angles": ["尝试调动五感：那里的空气闻起来是什么样的？脚下踩着什么质感的东西？", "考虑时间设定：这个地方存在于过去、未来，还是一个超越时空的维度？", "思考情感联结：为什么这个地方会反复出现在你的梦中？它在向你传达什么？"], "examples": ["「那是一片永远悬停在黄昏时度的草原，天空呈现出渐变的紫罗兰色，草尖上挂着的露珠在即将沉没的日光中闪烁。\"", "「这是一座漂浮在云端之上的图书馆，书架无限延伸，每一本书都记载着某个人的记忆。」"]}'),

('2', '颜色的声音', '如果在这个世界上，颜色有声音，那蓝色听起来像什么？', 'creative', '{"angles": ["通感描写：蓝色是尖锐的高音，还是低沉的叹息？是乐器的声音，还是自然之声？", "情绪联想：当你听到「蓝色的声音」时，内心会产生怎样的感受？平静？忧伤？辽阔？", "场景设定：不同场景下的蓝色声音是否不同？比如海洋的蓝、天空的蓝、深夜的蓝。"], "examples": ["「蓝色听起来像是大提琴最轻柔的那一弓，在空旷的音乐厅里缓缓荡开，带着微微的颤音。\"", "「蓝色，是雨滴敲击玻璃窗的节奏，清脆却不刺耳，像一首没有歌词的摇篮曲。」"]}'),

('3', '给十年前的自己', '给十年前的自己写一张 50 字的明信片。', 'reflection', '{"angles": ["时间视角：站在现在回望过去，你最想告诉那时的自己什么？", "情感表达：是鼓励、安慰、提醒，还是仅仅是问候？", "字数限制：50 字很珍贵，每一字都要有分量，你会选择哪些关键信息？"], "examples": ["「亲爱的我：别怕那些选择，每一个转弯都带你来到此刻。保持善良，也要学会保护自己。十年后的你，还不错。」", "「嘿，别那么焦虑。你现在担心的事，十年后大多不成问题。好好享受此刻的阳光吧。」"]}'),

('4', '老物件的故事', '描述一件你家中存放超过十年的老物件，讲述它见证的故事。', 'emotion', '{"angles": ["物象描写：仔细观察这件物品的细节——它的质感、颜色、痕迹。", "时间叙事：它见证了你家中的哪些重要时刻？经历了怎样的变迁？", "情感联结：为什么这件物品被保留下来？它对你意味着什么？"], "examples": ["「那把旧藤椅的扶手已经磨得发亮，藤条间夹杂着我小时候塞进去的图画纸。每次坐上去，它都会发出轻微的吱呀声，像是一位老人在回忆往事。」"]}'),

('5', '如果时间可以暂停', '如果有一天你获得了暂停时间的能力，你会选择在哪个时刻按下暂停键？', 'philosophical', '{"angles": ["场景选择：是珍贵的欢聚时刻？是宁静的独处时光？还是某个特别的瞬间？", "行为描述：暂停的时间里，你会做什么？观察？思考？还是仅仅是享受？", "深层思考：为什么选择这个时刻？它对你意味着什么？"], "examples": ["「我会选择在家人围坐餐桌的那个傍晚暂停。热气腾腾的饭菜，父亲的笑声，母亲唠叨的日常，所有美好都凝固在那个瞬间，我可以在房间里慢慢走动，记住每一张脸上的表情。」"]}'),

('6', '未寄出的信', '写一封你永远不会寄出的信。', 'emotion', '{"angles": ["收信人：可以是一个已经失去联系的人、过去的自己，甚至是未来的某人。", "情感表达：因为知道不会寄出，所以可以更坦诚。你想说哪些平时无法说出口的话？", "写作风格：是温柔的、愤怒的、遗憾的，还是释然的？"], "examples": ["「有些话当时没有说，后来就再也没机会说了。今天，我决定把它们写下来，不是为了寄给你，而是为了放过我自己。」"]}'),

('7', '雨夜咖啡馆', '在一个雨夜的咖啡馆里，你注意到了对面坐着的陌生人。描述你观察到的细节。', 'creative', '{"angles": ["观察细节：这个人的外貌、动作、神态有什么特别之处？", "想象空间：你对他/她有什么想象？他在等待什么？在逃避什么？", "氛围营造：雨夜的咖啡馆有怎样独特的氛围？"], "examples": ["「窗外的雨把世界模糊成一片水彩，咖啡馆里流淌着爵士乐。他对面的座位一直空着，咖啡已经凉了，但他每隔几分钟还是会看一眼手机，然后又放下。」"]}'),

('8', '假如生命只剩一天', '如果得知生命只剩下二十四小时，你会如何度过？', 'philosophical', '{"angles": ["优先排序：在这最后一天里，什么对你最重要？", "具体行动：不是泛泛而谈，而是具体描述你会做什么、见谁、去哪里。", "情感处理：恐惧、平静、遗憾、释然——你会经历怎样的心路历程？"], "examples": ["「我不会去旅行或完成什么宏大愿望。我会回家，给爸妈做顿饭，翻看旧相册，和最好的朋友通个电话。在平凡中，完成最后的告别。」"]}'),

('9', '城市里的秘密角落', '写下你在这座城市里最喜欢的一个小众角落，以及它对你的意义。', 'emotion', '{"angles": ["场景描写：这个地方有什么特别之处？为什么它不被大多数人发现？", "情感联结：你是如何发现它的？它陪伴你度过了哪些时光？", "氛围营造：描述这里的气味、声音、光线，让读者仿佛身临其境。"], "examples": ["「那家藏在巷子深处的小书店，老板养了一只总是睡觉的橘猫。我考研的那些日子，每天下午都坐在角落里。后来我考上了，还是会常回来，像是一种仪式。」"]}'),

('10', '与未来的对话', '想象十年后的你坐在面前，你们会聊些什么？', 'reflection', '{"angles": ["对话内容：你会问未来的自己什么问题？他/她会如何回答？", "对比变化：想象十年后的自己在外表、心态、生活上有什么变化。", "情感表达：是期待？恐惧？好奇？还是平静的接受？"], "examples": ["「我问他：你过上自己想要的生活了吗？他笑了笑，说：生活的答案永远在路上，但我学会了不再问终点在哪里，而是享受每一步。」"]}'),

('11', '童年的味道', '描述一种让你想起童年的味道，以及与之相关的记忆。', 'emotion', '{"angles": ["感官描写：这种味道是怎样的？是食物的香气、自然的气息，还是某种特别的味道？", "记忆联结：它让你想起童年的哪些场景？谁在你身边？发生了什么？", "情感表达：回忆起这段童年时光，你内心涌起怎样的情感？"], "examples": ["「是外婆家灶台上熬的红豆汤的味道。每次闻到这股甜香，我就看见夏天的午后，知了在窗外叫个不停，外婆在厨房忙碌，我坐在小板凳上看书。」"]}'),

('12', '如果文字有颜色', '如果每个汉字都有对应的颜色，你觉得「爱」「恨」「希望」「遗憾」分别是什么颜色？', 'creative', '{"angles": ["通感联想：为什么这些字会对应特定的颜色？是基于字形、读音，还是含义？", "情感表达：每种颜色的饱和度、明暗如何体现这个字的情感内涵？", "延伸思考：文字的颜色会随着语境变化吗？"], "examples": ["「『爱』是暖橙色，像清晨透过窗帘的第一缕阳光；『希望』是嫩绿色，像春天破土而出的新芽；『遗憾』是淡紫色，美丽却带着忧伤。」"]}')

ON CONFLICT (id) DO NOTHING;
