-- Seed topics with balanced mix of categories
-- 9 self-exploration, 12 creative writing, 8 reflection, 5 philosophical

INSERT INTO public.topics (id, title, prompt, category, inspiration) VALUES
-- Self-Exploration Topics (9)
('1', '内心独白', '写下此刻你内心最真实的声音，不必修饰，不必讨好。', 'emotion', $${"angles": ["此刻最真实的感受是什么？", "有什么话一直想说却没有说出口？", "如果不被任何人看见，你会写下什么？"], "examples": ["今天我感到一种莫名的疲惫，不是身体的累，而是心里的累...", "我一直以为我想要的那个东西很重要，但此刻我突然发现..."]}$$::jsonb),
('2', '情绪的形状', '描述一种你最近体验到的强烈情绪，它是什么颜色、形状、温度？', 'emotion', $${"angles": ["这种情绪在身体的哪个部位？", "它像什么自然现象？", "它来的时候，你通常在做什么？"], "examples": ["愤怒像一团火，从胸口升起，烧得我无法呼吸...", "悲伤是蓝色的液体，慢慢淹没了我的脚踝..."]}$$::jsonb),
('3', '安全感', '什么时候你感到最安全？描述那个场景和感觉。', 'emotion', $${"angles": ["是一个人、一个地方，还是一种状态？", "这种感觉如何被打破？", "如何找回这种安全感？"], "examples": ["小时候躲在妈妈的怀抱里，那时候的世界很小很安全...", "现在只有在深夜写字的时候，我才感觉找回了自己..."]}$$::jsonb),
('4', '脆弱的时刻', '写下最近一次你感到脆弱的时刻，不要回避。', 'emotion', $${"angles": ["当时发生了什么？", "你如何应对这种脆弱？", "脆弱中是否也藏着力量？"], "examples": ["那一刻我突然意识到，原来我并没有我想象中那么坚强...", "在脆弱中，我看见了真实的自己..."]}$$::jsonb),
('5', '被看见', '描述一次你真正被理解、被看见的经历。', 'emotion', $${"angles": ["那个人说了什么或做了什么？", "你当时的感觉如何？", "为什么这次经历如此特别？"], "examples": ["她没有给我任何建议，只是说「我懂」，那一刻我哭了...", "他看穿了我说的的话，直接看到了我没说的话..."]}$$::jsonb),
('6', '未完成的对话', '有一场你从未完成但一直想进行的对话，写下它。', 'reflection', $${"angles": ["对方是谁？", "你想说什么？", "为什么这场对话从未发生？"], "examples": ["我想对去世的爷爷说...我想告诉你，我成为了你想让我成为的人...", "我从未对父母说过的是：谢谢你们，但我选择不同的人生..."]}$$::jsonb),
('7', '身体的记忆', '你的身体记得什么？描述一次身体比意识更诚实的时刻。', 'emotion', $${"angles": ["身体的哪个部位有记忆？", "这种记忆如何影响你？", "如何倾听身体的声音？"], "examples": ["每次想起那个人，我的心跳就会加速...", "我的肩膀总是紧绷着，好像一直背着什么..."]}$$::jsonb),
('8', '秘密的花园', '描述一个只有你知道的精神角落，你去那里做什么？', 'reflection', $${"angles": ["这个地方是什么样子的？", "你什么时候会去那里？", "那里有没有你不想面对的东西？"], "examples": ["在我的秘密花园里，时间静止，我可以重新做选择...", "那里住着小时候的我，她从未长大..."]}$$::jsonb),
('9', '与恐惧对话', '写下你最大的恐惧，试着与它对话。', 'emotion', $${"angles": ["这种恐惧长什么样？", "它想告诉你什么？", "如何与它共处？"], "examples": ["我一直在逃避成功，因为害怕高处不胜寒...", "恐惧不是敌人，它只是想保护我..."]}$$::jsonb),

-- Creative Writing Topics (12)
('10', '梦境之地', '描述一个你反复梦到的奇幻之地，或者创造一个梦中世界。', 'imagination', $${"angles": ["这个世界有什么规则？", "那里住着什么样的生物？", "你在这个世界的角色是什么？"], "examples": ["天空是紫色的，河流流淌着星光...", "那里没有重力，人们住在漂浮的岛屿上..."]}$$::jsonb),
('11', '颜色的声音', '如果你能听到颜色的声音，它们会说什么？', 'creative', $${"angles": ["红色听起来像什么？", "蓝色会唱什么歌？", "不同颜色的对话会是怎样的？"], "examples": ["红色是小提琴的高音，热烈而尖锐...", "蓝色是大提琴的低吟，深沉而悠远..."]}$$::jsonb),
('12', '钥匙', '你捡到了一把古老的钥匙，它能打开什么？', 'imagination', $${"angles": ["这把钥匙长什么样？", "它原本属于谁？", "打开门后，你会看到什么？"], "examples": ["钥匙上刻着日期：1923年...", "门后是我童年的家，一切都保持着原样..."]}$$::jsonb),
('13', '雨夜咖啡馆', '描述一个雨夜的咖啡馆，窗外的世界模糊不清，窗内正在发生什么？', 'creative', $${"angles": ["咖啡馆里有什么样的人？", "窗外的雨象征着什么？", "此刻有一场重要的相遇吗？"], "examples": ["她坐在角落，等待着永远不会来的人...", "雨把世界隔绝在外，这里只剩下我和我的孤独..."]}$$::jsonb),
('14', '物品的视角', '选择一个身边的物品，以它的视角讲述它看到的故事。', 'creative', $${"angles": ["这个物品是什么？", "它见过什么？", "它对主人有什么感受？"], "examples": ["我是一支钢笔，我用墨水记录了他所有的秘密...", "我是一面镜子，我看着她每天变老..."]}$$::jsonb),
('15', '书店的一角', '描述一家神秘书店的角落，那里有什么不为人知的秘密？', 'imagination', $${"angles": ["这个角落有什么特别之处？", "谁会来这里？", "这里藏着什么魔法？"], "examples": ["那排书架上的书都是空白的，等待着读者填写...", "角落里有一个老人，他已经在这里坐了五十年..."]}$$::jsonb),
('16', '动物的眼睛', '描述通过一只动物的眼睛看到的人类世界。', 'imagination', $${"angles": ["这是什么动物？", "它如何看待人类？", "它理解或不理解什么？"], "examples": ["我是一只猫，人类总是自以为是地爱我...", "我是一只鸟，你们建造的城市对你们来说是牢笼..."]}$$::jsonb),
('17', '季节的告别', '拟人化地描述一个季节向另一个季节的告别。', 'creative', $${"angles": ["这是一个什么季节的告别？", "它们会说些什么？", "这次告别带着什么情绪？"], "examples": ["秋天对冬天说：带我一起走吧，我不想独自凋零...", "春天来得太迟，冬天已经忘记了如何放手..."]}$$::jsonb),
('18', '如果文字有颜色', '你想写的所有文字都有颜色，描述这样一篇文章。', 'creative', $${"angles": ["不同情绪的文字是什么颜色？", "这些颜色如何交织？", "读者看到这篇文章会看到什么？"], "examples": ["她的名字是金色的，每一次出现都照亮了纸面...", "悲伤的段落是灰色的，但希望是淡绿色的小芽..."]}$$::jsonb),
('19', '城市的声音', '描述一座城市的声音景观，从清晨到深夜。', 'creative', $${"angles": ["这座城市最特别的声音是什么？", "不同时段的声音如何变化？", "这些声音讲述什么故事？"], "examples": ["清晨是第一班地铁的轰鸣，深夜是孤独的脚步声...", "这座城市的声音从不停止，但我学会了在噪音中听见寂静..."]}$$::jsonb),
('20', '如果拥有超能力', '你可以拥有一种超能力，但只能使用一次。你会如何使用？', 'imagination', $${"angles": ["这是什么超能力？", "为什么只能使用一次？", "你会为什么而使用它？"], "examples": ["我可以让时间倒流，但我只回到了那个雨天...", "我想让一个人重新活一次，但我选择了谁？..."]}$$::jsonb),
('21', '秘密花园', '描述一个隐藏在城市中的秘密花园，只有少数人知道它的存在。', 'imagination', $${"angles": ["这个花园在哪里？", "谁拥有这里的钥匙？", "走进花园的人会发生什么？"], "examples": ["在地铁的某个站点，有一扇看不见的门...", "花园里开着一年四季所有的花，时间在这里不适用..."]}$$::jsonb),

-- Reflection & Growth Topics (8)
('22', '给十年前的自己', '如果可以给十年前的自己写一封信，你会说什么？', 'reflection', $${"angles": ["那时的你最需要听到什么？", "你想纠正什么选择？", "还是你会说，一切都好？"], "examples": ["亲爱的十年前的我，不要害怕失败，那是成长的必经之路...", "我想说，你走的每一步都是对的，即使当时看来是弯路..."]}$$::jsonb),
('23', '与未来的对话', '想象五年后的你，坐在你面前，你们会聊什么？', 'reflection', $${"angles": ["五年后的你是什么样子的？", "你问了她什么问题？", "她的回答让你惊讶吗？"], "examples": ["我问她是否值得，她笑着说，从来没有后悔...", "她说，你现在担心的一切，到时候都不重要了..."]}$$::jsonb),
('24', '放下', '描述一次你真正放下的经历，那个过程是怎样的？', 'reflection', $${"angles": ["你需要放下什么？", "放下的转折点是什么？", "放下后你感到什么？"], "examples": ["我不是原谅了，我只是不再让那件事伤害我...", "放下不是遗忘，而是想起时不再心痛..."]}$$::jsonb),
('25', '改变', '描述一次你彻底改变的时刻。', 'reflection', $${"angles": ["是什么触发了这个改变？", "你变成了什么样的人？", "你怀念改变前的自己吗？"], "examples": ["那个下午，我突然决定不再讨好任何人...", "我以为改变会很难，但其实只需要一个决定..."]}$$::jsonb),
('26', '遗憾', '写下你最大的遗憾，或者，你为什么没有遗憾。', 'reflection', $${"angles": ["这件事真的可以改变吗？", "遗憾教会了你什么？", "如果重来，你会做不同选择吗？"], "examples": ["我遗憾没有早点说出那三个字...", "我最大的遗憾是，我从未为自己活过..."]}$$::jsonb),
('27', '感恩', '写下三件你今天感恩的事情，不管多小。', 'reflection', $${"angles": ["这些小事为什么重要？", "感恩如何改变你的视角？", "如何保持感恩的心态？"], "examples": ["感恩今天的阳光，感恩咖啡的温度，感恩还在呼吸...", "我发现，当我开始感恩，我的世界变大了..."]}$$::jsonb),
('28', '选择', '描述一个改变你人生轨迹的重要选择。', 'reflection', $${"angles": ["你做了什么选择？", "如果选了另一条路？", "这个选择塑造了今天的你什么？"], "examples": ["那天我选择了离开家乡，从那以后，家成了远方...", "我选择了安逸，但我不知道那是安逸还是妥协..."]}$$::jsonb),
('29', '孤独', '描述你与孤独的关系。', 'reflection', $${"angles": ["孤独和独处有什么区别？", "你什么时候感到最孤独？", "孤独可以是一种享受吗？"], "examples": ["孤独不是一个人，而是一种与世界失去联系的感觉...", "我学会了享受独处，那是与自己对话的最好时光..."]}$$::jsonb),

-- Philosophical Topics (5)
('30', '如果时间可以暂停', '如果时间可以在某个时刻暂停一小时，只有你意识到并可以行动，你会做什么？', 'philosophical', $${"angles": ["你会选择哪个时刻？", "你会去见谁？", "这一小时会改变什么？"], "examples": ["我会回到那场告别，好好拥抱他...", "我想在某个平凡下午，多看一会儿夕阳..."]}$$::jsonb),
('31', '假如生命只剩一天', '如果今天是你生命的最后一天，你会做什么？', 'philosophical', $${"angles": ["你会去哪里？", "你会见谁？", "你最后想说的话是什么？"], "examples": ["我会写一封信给每个人，告诉他们我爱他们...", "我会回到海边，看最后一次日出..."]}$$::jsonb),
('32', '如果时间倒流', '如果可以回到过去，但只能观察不能改变，你会回到什么时候？', 'philosophical', $${"angles": ["你想看什么？", "你想见谁？", "不能改变会让你痛苦吗？"], "examples": ["我想回到童年，看那个被遗忘的自己...", "我会回到遇见她的那一天，重新记住每一个细节..."]}$$::jsonb),
('33', '人生的剧本', '如果人生是一出戏，你觉得这出戏的主题是什么？', 'philosophical', $${"angles": ["这是一出喜剧、悲剧，还是什么？", "你在扮演什么角色？", "这个角色是你自己选择的吗？"], "examples": ["我的人生是一部荒诞剧，充满了无厘头的转折...", "我以为我是主角，后来发现我只是一个配角..."]}$$::jsonb),
('34', '理想与现实', '描述你的理想生活与现实生活的差距。', 'philosophical', $${"angles": ["理想的生活是什么样子的？", "现实生活是什么样子的？", "如何缩小这个差距？"], "examples": ["理想中的我住在森林里，现实中的我住在格子间...", "我从未放弃理想，但我学会了与之共存..."]}$$::jsonb);

-- Create indexes for better query performance
CREATE INDEX IF NOT EXISTS idx_topics_category ON public.topics(category);
CREATE INDEX IF NOT EXISTS idx_topics_created_at ON public.topics(created_at DESC);
